import React, { useState } from "react";
import { Link } from "react-router-dom";
import './It1.css';

const Mern = () => {


    const courses = [

        { id: 1, title: "React_JS", category: "React JS hands on Playlist", imageSrc: "data:image/png;base64," },
        { id: 2, title: "MongoDb Database", category: "MongoDb Database hands on Playlist", imageSrc: "data:image/png;base64," },
        { id: 3, title: "Node JS", category: "Node JS hands on Playlist", imageSrc: "data:image/png;base64," },
        { id: 4, title: "Express JS", category: "Express JS hands on Playlist", imageSrc: "data:image/png;base64," },
        // Add more courses as needed
    ];

    const uniqueCategories = ['All Subjects', ...new Set(courses.map(course => course.category))];

    const [selectedCategory, setSelectedCategory] = useState('All Subjects');

    const handleCategoryClick = (category) => {
        setSelectedCategory(category);
    };

    const filteredCourses = selectedCategory === 'All Subjects'
        ? courses
        : courses.filter(course => course.category === selectedCategory);

    return (

        <div className="it-container-fluid">

            {/* <nav>
                    <Navbar />
                </nav> */}
            <div className="main">
                <div className="sub">

                    <ul>
                        {uniqueCategories.map(category => (
                            <li
                                key={category}
                                onClick={() => handleCategoryClick(category)}
                                className={category === selectedCategory ? "active" : ""}
                            >
                                {category}
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="courses-container">
                    <h2 className="playcenter">Welcome To MERN Stack Tutorial By EduNexus</h2>
                    <div className="courses">
                        {filteredCourses.map(course => (
                            <div className="card" key={course.id}>
                                <div className="row no-gutters">
                                    <div className="col-md-4">
                                        <img src={course.imageSrc} alt={course.title} />
                                    </div>
                                    <div className="col-md-8">
                                        <div className="card-body">
                                            <h5 className="card-title">{course.title}</h5>
                                            <p className="card-text">{course.category}</p>
                                            <Link to={`/${course.title}`}>
                                                <button type="button" class="btn-warning but">Watch Now</button>
                                            </Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

        </div>

    )
}

export default Mern